import React from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import {useState, useEffect } from 'react'
import axios from 'axios'
import imgStyle from "./Main.module.css"


const ViewOne = (props) => {
 //grab the url variable
    const {id} = useParams();
    const navigate = useNavigate();

    const [thisPirate, setThisPirate] = useState(null)
    const [pegLeg, setPegLeg] = useState()
    const [eyePatch, setEyePatch] = useState()
    const [hookHand, setHookHand] = useState()

useEffect(() => {
    axios.get(`http://localhost:8000/api/pirates/${id}`)
    .then(response => {
        console.log(response.data)
        setThisPirate(response.data)
    })
    .catch(error => {
        console.log(error)
    })
},[id])

useEffect(
    () => {
    document.body.style.background = '#ff9900'
    getPirate()
    handleEdit()
    },
    [eyePatch,hookHand,pegLeg]
)

const handleEye = () => {
    if(eyePatch){
        setEyePatch(false)
    }else{
        setEyePatch(true)
    }
}

const handleLeg = () => {
    if(pegLeg){
        setPegLeg(false)
    }else{
        setPegLeg(true)
    }
}
const handleHand = () => {
    if(hookHand){
        setHookHand(false)
    }else{
        setHookHand(true)
    }
}


    return (
    <div>
    {thisPirate ? (
        <>
        <div>
            <h1>{thisPirate.name}</h1>
        </div>
            <div>
                <img src={thisPirate.image} alt="where did you go"  className={imgStyle.img}/>
                <h2>{thisPirate.catchPhrase}</h2>
            </div>
            <div>
                <h3>About</h3>
                <p>Position:{thisPirate.crewPosition}</p>
                <p>Treasures:{thisPirate.treasure}</p>
                <p>Peg Leg:{thisPirate.pegLeg ? "Yes" : ""} <button onClick={(e) => handleLeg(e)}>{thisPirate.pegLeg ? "Yes" : "No"}</button></p>
                <p>Eye Patch:{thisPirate.eyePatch ? "Yes" : ""} <button onClick={(e) => handleEye(e)}>{thisPirate.pegLeg ? "Yes" : "No"}</button></p>
                <p>Hook Hand:{thisPirate.hookHand ? "Yes" : ""} <button onClick={(e) => handleHand(e)}>{thisPirate.pegLeg ? "Yes" : "No"}</button></p>
                </div>
        </>
        ): ("You have not joined the crew...")
    }
    <button onClick={() => navigate('/pirates')}>Home</button>
    </div>
    )
}


export default ViewOne
