import React from 'react'
import {useState} from 'react'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'

const Create = (props) => {

    //for redirect
    const navigate = useNavigate()

    // forms submit variables 
    const[name, setName] = useState("")
    const[image, setImage] = useState("")
    const[catchPhrase, setCatchPhrase] = useState("")

    //DB error array
    const [errors,setErrors] = useState([]);

    const createNote = (e) => {
        e.preventDefault();
        const tempObjToSendToDB = {
            title,
            content,
            isImportant
        }
        axios.post('http://localhost:8000/api/notes', tempObjToSendToDB)
        .then(response => {
            console.log("Client Success")
            console.log(response.data)
            navigate('/notes')
        })
        .catch(error => {
            console.log("Something Went Wrong")
            console.log(error)
            const errorResponse = error.response.data.errors; // Get the errors from err.response.data
                const errorArr = []; // Define a temp error array to push the messages in
                for (const key of Object.keys(errorResponse)) { // Loop through all errors and get the messages
                    errorArr.push(errorResponse[key].message)
                }
                // Set Errors
                setErrors(errorArr);
        }) 
    }
return (
    <div>
        <div>
            {errors.map((error,index) => <p key ={index}>{error}</p>)}
        </div>
        {/* form state variables: <br/>
        {JSON.stringify(title)}<br/>
        {JSON.stringify(content)}<br/>
        {JSON.stringify(isImportant)}<br/> */}
        <form onSubmit={createNote}>
            title:<input onChange={(e) => setTitle(e.target.value)} value={title}/><br/>
            content:<textarea onChange={(e) => setContent(e.target.value)}value={content}/><br/>
            Important?:<input type="checkbox" onChange={(e) => setIsImportant(e.target.checked)} checked={isImportant}/><br/>
            <button>Create</button>
        </form>
    </div>
)
}

export default Create
