import React from 'react'
import { useParams } from 'react-router-dom'
import {useState, useEffect} from 'react'
import axios from 'axios'
import imgStyle from "./Main.module.css"


const ViewOne = (props) => {
 //grab the url variable
    const {id} = useParams()

    const [thisPirate, setThisPirate] = useState(null)

useEffect(() => {
    axios.get(`http://localhost:8000/api/pirates/${id}`)
    .then(response => {
        console.log(response.data)
        setThisPirate(response.data)
    })
    .catch(error => {
        console.log(error)
    })
},[id])


    return (
    <div>
    {thisPirate ? (
        <>
        <div>
            <h1>{thisPirate.name}</h1>
        </div>
            <div>
                <img src={thisPirate.image} alt="where did you go"  className={imgStyle.img}/>
            </div>
            <div>
                <h3>About</h3>
                <p>Position:{thisPirate.crewPosition}</p>
                <p>Treasures:{thisPirate.treasures}</p>
                <p>Peg Leg:{thisPirate.pegLeg ? "Yes" : ""}</p>
                <p>Eye Patch:{thisPirate.eyePatch ? "Yes" : ""}</p>
                <p>Hook Hand:{thisPirate.hookHand ? "Yes" : ""}</p>
                </div>
        </>
        ): ("Loading...")
    }
    </div>
    )
}


export default ViewOne
